    local function _2038_SelfSectionFunc()
        if (lua_Settings.Self_SetMaxHealth) then
            local _ped = PLAYER.PLAYER_PED_ID()
            local _maxHP = ENTITY.GET_ENTITY_MAX_HEALTH(_ped)
            ENTITY.SET_ENTITY_HEALTH(_ped, _maxHP, 0)

            lua_Settings.Self_SetMaxHealth = false
        end

        if (lua_Settings.Self_SetMaxArmor) then
            local _ped = PLAYER.PLAYER_PED_ID()
            PED.SET_PED_ARMOUR(_ped, 100)

            lua_Settings.Self_SetMaxArmor = false
        end

        if (lua_Settings.Self_IsAutoRegeneration) then
            local _ped = PLAYER.PLAYER_PED_ID()

            if (ENTITY.GET_ENTITY_HEALTH(_ped) < lua_Settings.Self_AutoRegenerationMin) then
                lua_Settings.Self_SetMaxHealth = true
            end

            if (PED.GET_PED_ARMOUR(_ped) < lua_Settings.Self_AutoRegenerationMin) then
                lua_Settings.Self_SetMaxArmor = true
            end
        end

        if (lua_Settings.Self_IsWantedLevel) then
            local _player_id = PLAYER.PLAYER_ID()
            PLAYER.SET_PLAYER_WANTED_LEVEL(_player_id, lua_Settings.Self_WantedLevelValue, false)
            PLAYER.SET_PLAYER_WANTED_LEVEL_NOW(_player_id, false)

            lua_Settings.Self_IsWantedLevel = false
        end

        if (lua_Settings.Self_IsFakeWantedLevel) then
            MISC.SET_FAKE_WANTED_LEVEL(lua_Settings.Self_WantedLevelValue)

            lua_Settings.Self_IsFakeWantedLevel = false
        end

        if (lua_Settings.Self_IsTeleportTo) then
            if (__tempSelf.TeleportPreset.selected == 0) then
                lua_Settings.Self_IsTeleportTo = false
                return            
            end

            local _table = __tempSelf.TeleportPreset.sorting[__tempSelf.TeleportPreset.list[lua_Settings.Self_TeleportPresetSelected]]
            local _coords = _table[__tempSelf.TeleportPreset.selected]
            entity_teleport(PLAYER.PLAYER_PED_ID(), _coords.X, _coords.Y, _coords.Z)

            lua_Settings.Self_IsTeleportTo = false
        end

        if (lua_Settings.Self_IsNoClip) then
            local _ped = PLAYER.PLAYER_PED_ID()
            local _vehicle = PED.GET_VEHICLE_PED_IS_USING(_ped)

            if (PED.IS_PED_IN_VEHICLE(_ped, _vehicle, true)) then
                _ped = _vehicle
            end

            local _coords = ENTITY.GET_ENTITY_COORDS(_ped, true)
            local _rot = CAM.GET_GAMEPLAY_CAM_ROT(2)
            ENTITY.SET_ENTITY_ROTATION(_ped, _rot.x, _rot.y, _rot.z, 2, true)

            if PAD.IS_CONTROL_PRESSED(2, 21) then --[[ Shift ]]
                __NoClipSettings.Boost = lua_Settings.Self_NoClipSpeed
            else
                __NoClipSettings.Boost = lua_Settings.Self_NoClipSpeed / 10
            end

            if PAD.IS_CONTROL_PRESSED(2, 87) then --[[ W ]]
                __NoClipSettings.y = __NoClipSettings.Boost
            elseif not PAD.IS_CONTROL_PRESSED(2, 88) then
                __NoClipSettings.y = 0
            end

            if PAD.IS_CONTROL_PRESSED(2, 88) then --[[ S ]]
                __NoClipSettings.y = -__NoClipSettings.Boost
            elseif not PAD.IS_CONTROL_PRESSED(2, 87) then
                __NoClipSettings.y = 0
            end

            if PAD.IS_CONTROL_PRESSED(2, 89) then --[[ A ]]
                __NoClipSettings.x = -__NoClipSettings.Boost
            elseif not PAD.IS_CONTROL_PRESSED(2, 90) then
                __NoClipSettings.x = 0
            end

            if PAD.IS_CONTROL_PRESSED(2, 90) then --[[ D ]]
                __NoClipSettings.x = __NoClipSettings.Boost
            elseif not PAD.IS_CONTROL_PRESSED(2, 89) then
                __NoClipSettings.x = 0
            end
            
            if PAD.IS_CONTROL_PRESSED(2, 22) then --[[ Space ]]
                __NoClipSettings.z = __NoClipSettings.Boost / 3
            elseif not PAD.IS_CONTROL_PRESSED(2, 132) then
                __NoClipSettings.z = 0
            end

            if PAD.IS_CONTROL_PRESSED(2, 132) then --[[ CTRL ]]
                __NoClipSettings.z = -__NoClipSettings.Boost / 3
            elseif not PAD.IS_CONTROL_PRESSED(2, 22) then
                __NoClipSettings.z = 0
            end

            local _worldOffset = ENTITY.GET_OFFSET_FROM_ENTITY_IN_WORLD_COORDS(_ped, __NoClipSettings.x, __NoClipSettings.y, __NoClipSettings.z)
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(_ped, _worldOffset.x, _worldOffset.y, _worldOffset.z, true, true, true)

            lua_Settings.Self_SetMaxHealth = true
        end

        if (lua_Settings.Self_IsAlpha) then
            ENTITY.SET_ENTITY_ALPHA(PLAYER.PLAYER_PED_ID(), lua_Settings.Self_AlphaValue)
        end
    end

