    local function _2038_RecoverySectionFunc(_s)
        if (NETWORK.NETWORK_IS_IN_SESSION()) then

            if (lua_Settings.Recovery_IsDisableTransactionError) then
                globals.set_int(4537357, 0)
                globals.set_int(4537358, 0)
                globals.set_int(4537356, 0)
            end

            if (lua_Settings.Recovery_Is1mLoop) then
                trg_transaction(0x615762F1, 1000000)
            end

            if (lua_Settings.Recovery_Is1mOnce) then
                trg_transaction(0x615762F1, 1000000)
                lua_Settings.Recovery_Is1mOnce = false
            end

            if (lua_Settings.Recovery_Is50kLoop) then
                trg_transaction(joaat("SERVICE_EARN_YOHAN_SOURCE_GOODS"), 50000)
            end

            if (lua_Settings.Recovery_IsCasinoBonus or lua_Settings.Recovery_IsCasinoBonusOnce) then
                globals.set_int(1963515, 1)
                lua_Settings.Recovery_IsCasinoBonusOnce = false
