    local function _2038_SpawnerSectionFunc()
        if (_rotation360 == 360) then
            _rotation360 = 0
        elseif (_rotation360 ~= 360) then
            _rotation360 = _rotation360 + 0.5
        end

        if (lua_Settings.Spawner_IsVehiclePreview) then
            if (__tempSpawner.vehicles.selected ~= 0) then
                local _ped = PLAYER.PLAYER_PED_ID()
                local _vehicle = PED.GET_VEHICLE_PED_IS_USING(_ped)
                local _prevHash = joaat(__sortingVehicle[__tempSpawner.vehicles.selected].hash)

                if (STREAMING.IS_MODEL_VALID(_prevHash)) then
                    local _coords = ENTITY.GET_OFFSET_FROM_ENTITY_IN_WORLD_COORDS(_ped, 0.0, 5.0, -0.5)
                    if (not ENTITY.DOES_ENTITY_EXIST(__tempSpawner.vehicles.preview)) then
                        if (not STREAMING.HAS_MODEL_LOADED(_prevHash)) then
                            STREAMING.REQUEST_MODEL(_prevHash)
                        end

                        __tempSpawner.vehicles.preview = VEHICLE.CREATE_VEHICLE(_prevHash, _coords.x, _coords.y, _coords.z, _rotation360, false, true, false)
                        ENTITY.SET_ENTITY_COLLISION(__tempSpawner.vehicles.preview, false, false)
                        ENTITY.SET_CAN_CLIMB_ON_ENTITY(__tempSpawner.vehicles.preview, false)
                        OBJECT.SET_OBJECT_ALLOW_LOW_LOD_BUOYANCY(__tempSpawner.vehicles.preview, false)
                    end

                    if (__tempSpawner.vehicles.preview) then
                        local __hash = ENTITY.GET_ENTITY_MODEL(__tempSpawner.vehicles.preview)

                        if (__hash ~= _prevHash) then
                            entity_remove(__tempSpawner.vehicles.preview)

                            __tempSpawner.vehicles.preview = 0
                        end
        
                        if (PED.IS_PED_IN_VEHICLE(_ped, _vehicle, true)) then
                            _ped = _vehicle
                        end                

                        ENTITY.SET_ENTITY_COORDS_NO_OFFSET(__tempSpawner.vehicles.preview, _coords.x, _coords.y, _coords.z + 1, false, false, false)
                        ENTITY.SET_ENTITY_HEADING(__tempSpawner.vehicles.preview, _rotation360)
                        ENTITY.SET_ENTITY_NO_COLLISION_ENTITY(__tempSpawner.vehicles.preview, _ped, false)
                        ENTITY.SET_ENTITY_COLLISION(__tempSpawner.vehicles.preview, false, false)
                        ENTITY.SET_CAN_CLIMB_ON_ENTITY(__tempSpawner.vehicles.preview, false)
                        OBJECT.SET_OBJECT_ALLOW_LOW_LOD_BUOYANCY(__tempSpawner.vehicles.preview, false)
                        ENTITY.SET_ENTITY_ALPHA(__tempSpawner.vehicles.preview, 240, false)
                    end
                end
            else
                if (__tempSpawner.vehicles.preview ~= 0) then
                    entity_remove(__tempSpawner.vehicles.preview)
                    __tempSpawner.vehicles.preview = 0
                end
            end

        end

        if (lua_Settings.Spawner_IsVehicleSpawn) then
            if __tempSpawner.vehicles.selected == 0 then lua_Settings.Spawner_IsVehicleSpawn = false end
            local _name = __sortingVehicle[__tempSpawner.vehicles.selected].hash
            if _name == nil then lua_Settings.Spawner_IsVehicleSpawn = false end

            local _hash = joaat(_name)
            local _ped = PLAYER.PLAYER_PED_ID()
            local _coords = ENTITY.GET_OFFSET_FROM_ENTITY_IN_WORLD_COORDS(_ped, 0.0, 5.0, -0.5)
            local _rotation = ENTITY.GET_ENTITY_ROTATION(_ped)

            if (not STREAMING.HAS_MODEL_LOADED(_hash)) then
                STREAMING.REQUEST_MODEL(_hash)
            else
                __tempSpawner.vehicles.spawned = VEHICLE.CREATE_VEHICLE(_hash, _coords.x, _coords.y, _coords.z, _rotation.z, true, true, false)
                table.insert(_vehList.id, tostring(__tempSpawner.vehicles.spawned))
                table.insert(_vehList.name, tostring(__tempSpawner.vehicles.spawned))
                table.insert(_vehList.hash, tostring(__tempSpawner.vehicles.spawned))
                

                if (lua_Settings.Spawner_IsMaintainSpeed) then
                    VEHICLE.SET_VEHICLE_ENGINE_ON(__tempSpawner.vehicles.spawned, true, true, false)
                    VEHICLE.SET_VEHICLE_FORWARD_SPEED(__tempSpawner.vehicles.spawned, ENTITY.GET_ENTITY_SPEED(_ped) + 1)
                end

                if (lua_Settings.Spawner_IsGiftVehicle) then
                    local _handle = NETWORK.NETWORK_HASH_FROM_PLAYER_HANDLE(PLAYER.PLAYER_ID())
                    DECORATOR.DECOR_SET_INT(__tempSpawner.vehicles.spawned, "Player_Vehicle", _handle)
                    DECORATOR.DECOR_SET_INT(__tempSpawner.vehicles.spawned, "Veh_Modded_By_Player", _handle)
                    DECORATOR.DECOR_SET_INT(__tempSpawner.vehicles.spawned, "PV_Slot", 2)
                    DECORATOR.DECOR_SET_INT(__tempSpawner.vehicles.spawned, "Previous_Owner", _handle)
                end

                if (lua_Settings.Spawner_IsSpawnInVehicle) then
                    PED.SET_PED_INTO_VEHICLE(_ped, __tempSpawner.vehicles.spawned, -1)
                end

                if (lua_Settings.Spawner_VehicleTuning == 1) then
                    vehicle_perfomance_tuning(__tempSpawner.vehicles.spawned)
                elseif (lua_Settings.Spawner_VehicleTuning == 2) then
                    vehicle_random_tuning(__tempSpawner.vehicles.spawned)
                end

                if (lua_Settings.Spawner_VehicleWheelType ~= 0) then
                    VEHICLE.SET_VEHICLE_MOD_KIT(__tempSpawner.vehicles.spawned, 0)

                    VEHICLE.SET_VEHICLE_WHEEL_TYPE(__tempSpawner.vehicles.spawned, lua_Settings.Spawner_VehicleWheelType)
                    local __wheels = math.random( -1, VEHICLE.GET_NUM_VEHICLE_MODS(__tempSpawner.vehicles.spawned, 23))
                    VEHICLE.SET_VEHICLE_MOD(__tempSpawner.vehicles.spawned, 23, __wheels, true)
                    VEHICLE.SET_VEHICLE_MOD(__tempSpawner.vehicles.spawned, 24, __wheels, true)
                end
                lua_Settings.Spawner_IsVehicleSpawn = false    
            end
        end 
    end

