            end

            if lua_Settings.Misc_AutoDriveType == 1 then
                TASK.TASK_VEHICLE_DRIVE_WANDER(_ped, _vehicle, _speed, _style)
            end
            lua_Settings.Misc_IsAutoDriveStart = false
        end

        if (___AutoDriveOn.on) then
            local _vehicle = PED.GET_VEHICLE_PED_IS_USING(PLAYER.PLAYER_PED_ID())
            local _coords = ENTITY.GET_ENTITY_COORDS(_vehicle, true)

            if (_coords.x == ___AutoDriveOn.x and _coords.y == ___AutoDriveOn.y and _coords.z == ___AutoDriveOn.z) then
                lua_Settings.Misc_IsAutoDriveStop = true
                ___AutoDriveOn.on = false
            end
        end

        if (lua_Settings.Misc_IsAutoDriveStop) then
            local _ped = PLAYER.PLAYER_PED_ID()
            local _vehicle = PED.GET_VEHICLE_PED_IS_USING(_ped)

            TASK.CLEAR_PED_TASKS_IMMEDIATELY(_ped)
            PED.SET_PED_INTO_VEHICLE(_ped, _vehicle, -1)
            lua_Settings.Misc_IsAutoDriveStop = false
        end

    end
    
