script.register_script("HadlerScripts_2038Ping", function(_s) 
    if (lua_Settings.Weapons_IsRainbowGun) then
        local _ped = PLAYER.PLAYER_PED_ID()
        local _selectedWeapon = WEAPON.GET_SELECTED_PED_WEAPON(_ped)
        local _tint = WEAPON.GET_PED_WEAPON_TINT_INDEX(_ped, _selectedWeapon)
    
        if _tint == 8 then _tint = 0 else _tint = _tint + 1 end
        WEAPON.SET_PED_WEAPON_TINT_INDEX(_ped, _selectedWeapon, _tint)
    end

    _s:sleep(150)
end)
